@section('plugins.Sweetalert2', true)
@section('adminlte_js')
    @stack('js')
    @yield('js')
    <script>
        function deleteAction(e) {
            e.preventDefault();
            Swal.fire({
                title: 'Are you sure?',
                text: "You won't be able to revert this!",
                type: 'warning',
                showCancelButton: true,
                confirmButtonColor: '#3085d6',
                cancelButtonColor: '#d33',
                confirmButtonText: 'Yes, delete it!'
            }).then((result) => {
                console.log(result);
                if (result.value) {
                    document.getElementById("deleteAction").submit();
                }
            })
        }
        function actionStatus(status, type) {
            Swal.fire({
                title: status,
                type: type,
            })
        }
    </script>
    @error('msg')
        <script>
            actionStatus("{{$message}}", "error")
        </script>
    @enderror
    @if(session('success'))
        <script>
            actionStatus("{{session('success')}}", "success")
        </script>               
    @endif
@stop
